//---------------------------Header Slider ---------------------------------//
(function () {
    document.addEventListener("DOMContentLoaded", function () {
        // Limit to the VividHeaderSlider widget only
        let container = document.querySelector("#VividHeroSection #VividHeaderSlider .widget-content");
        if (!container) return;

        let content = container.innerHTML;
        let regex = /\[vividSlider\/(.*?)\/(\d+)\]/g;

        container.innerHTML = content.replace(regex, function (match, type, number) {
            let blockId = "VividHeaderBlock_" + Math.random().toString(36).substr(2, 9);
            buildBlock(blockId, type);
            return `<div class="VividHeaderSingleWrapper" id="${blockId}"></div>`;
        });
    });

    function extractPricesFromBody(htmlContent) {
        let currentPrice = "";
        const currentMatch = htmlContent.match(/\[data-current-price="([^"]*)"\]/i);
        if (currentMatch) currentPrice = currentMatch[1].trim();
        return { currentPrice };
    }

    function buildBlock(blockId, type) {
        let blogUrl = window.location.origin;
        let feedUrl;

        if (type.toLowerCase() === "recent") {
            feedUrl = blogUrl + "/feeds/posts/default?alt=json&max-results=1";
        } else if (type.toLowerCase() === "random") {
            feedUrl = blogUrl + "/feeds/posts/default?alt=json&max-results=50";
        } else {
            feedUrl = blogUrl + "/feeds/posts/default/-/" + encodeURIComponent(type) + "?alt=json&max-results=1";
        }

        fetch(feedUrl)
            .then(res => res.json())
            .then(data => {
                let entries = data.feed.entry || [];
                if (!entries.length) {
                    document.querySelector("#" + blockId).innerHTML = "<p>No posts found.</p>";
                    return;
                }

                // If random, pick 1 random entry
                let entry = type.toLowerCase() === "random"
                    ? entries[Math.floor(Math.random() * entries.length)]
                    : entries[0];

                let title = entry.title.$t;
                let link = entry.link.find(l => l.rel === "alternate").href;
                let label = (entry.category && entry.category.length > 0) ? entry.category[0].term : "";

                // Get image
                let img = "https://via.placeholder.com/800x450?text=No+Image";
                if (entry.content && entry.content.$t) {
                    const tempDiv = document.createElement("div");
                    tempDiv.innerHTML = entry.content.$t;
                    const firstImg = tempDiv.querySelector("img");
                    if (firstImg) {
                        img = firstImg.src.replace(/\/w\d+-h\d+\//, '/s0/').replace(/\/s\d+(-c)?\//, '/s0/');
                    }
                }

                // Extract only current price
                let { currentPrice } = extractPricesFromBody(entry.content?.$t || "");

                let priceHTML = currentPrice ? `<span class="current-price">${currentPrice}</span>` : "";

                let blockHTML = `
                    <div class="single-post-block">
                        <div class="block-left">
                            ${label ? `<h4>${label}</h4>` : ""}
                            <h2>${title}</h2>
                            <div class="price-wrap">${priceHTML}</div>
                            <a class="view-btn" href="${link}" target="_blank">View Product</a>
                        </div>
                        <div class="block-right">
                            <img src="${img}" alt="${title}">
                        </div>
                    </div>
                `;

                document.querySelector("#" + blockId).innerHTML = blockHTML;
            })
            .catch(() => {
                document.querySelector("#" + blockId).innerHTML = "<p>Could not load post.</p>";
            });
    }
})();





/*---------------------Related Products -----------------------------*/

(function () {
  const uid = () => "id" + Math.random().toString(36).substr(2, 7);

  // --- Fetch posts by label ---
  function fetchPosts(label, count, cb) {
    const callback = "cb_" + uid();
    window[callback] = function (json) {
      const posts = [];
      if (json.feed && json.feed.entry) {
        json.feed.entry.forEach((e) => {
          const link = e.link.find((l) => l.rel == "alternate");
          posts.push({
            id: e.id.$t.replace(/.*post-(\d+)$/, "$1"),
            title: e.title.$t,
            url: link.href,
            body: e.content ? e.content.$t : e.summary ? e.summary.$t : "",
          });
        });
      }
      cb(posts);
      delete window[callback];
    };
    const s = document.createElement("script");
    s.src = `/feeds/posts/default/-/${encodeURIComponent(
      label
    )}?alt=json-in-script&max-results=${count}&callback=${callback}`;
    document.body.appendChild(s);
  }

  function getImageFromEntry(entry) {
    try {
      const content = entry.content?.$t || entry.summary?.$t || "";
      const temp = document.createElement("div");
      temp.innerHTML = content;

      const firstImg = temp.querySelector("img");
      if (firstImg) {
        let url = firstImg.src;
        url = url
          .replace(/\/s\d+(-c)?\//, "/s0/")
          .replace(/\/w\d+-h\d+\//, "/s0/");
        return url;
      }

      if (entry.media$thumbnail?.url) {
        let url = entry.media$thumbnail.url;
        url = url
          .replace(/\/s\d+(-c)?\//, "/s0/")
          .replace(/\/w\d+-h\d+\//, "/s0/");
        return url;
      }
    } catch (e) {}

    return "https://via.placeholder.com/400x300?text=No+Image";
  }

  // --- Parse product info ---
  function parseProduct(body) {
    const out = {};
    out.image = getImageFromEntry({ content: { $t: body } });

    const sale = body.match(/\[data-sale=(["'])(.*?)\1\]/i);
    out.sale = sale ? sale[2] : "";

    const curP = body.match(/\[data-current-price=(["'])(.*?)\1\]/i);
    out.curPrice = curP ? curP[2] : "";

    return out;
  }

  // --- Build simplified card ---
  function buildCard(p) {
    const prod = parseProduct(p.body);
    return `
    <div class="post-outer">
      <div class="vivid-post-homepage-view" data-post-id="${p.id}">
        <!-- Image -->
        <div class="homepage-main-image">
          ${prod.image ? `<img src="${prod.image}" alt="${p.title}">` : ""}
          ${prod.sale ? `<span class="vivid-product-sale">${prod.sale}</span>` : ""}
        </div>

        <!-- Product Details -->
        <div class="product-details">
          <h3 class="product-title entry-title">
            <a href="${p.url}">${p.title}</a>
          </h3>

          <div class="product-footer">
            <div class="product-price">
              ${prod.curPrice ? `<div class="vivid-product-current-price">${prod.curPrice}</div>` : ""}
            </div>
            <div class="vivid-product-actions">
              <a class="view-product-btn" href="${p.url}" title="View Product">
                <i class="bi bi-eye"></i> View Product
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>`;
  }

  // --- Init shortcode ---
  function init() {
    document.querySelectorAll(".widget-content").forEach((box) => {
      const sc = box.textContent.match(/\[vividRelated\/(\d+)\]/);
      if (!sc) return;

      // Always max 3 posts
      let count = 3;

      box.innerHTML = `<div class="related-products-grid"></div>`;
      const wrap = box.querySelector(".related-products-grid");

      const labels = Array.from(
        document.querySelectorAll(".vivid-meta-info .tag")
      ).map((x) => x.textContent.trim());

      if (!labels.length) {
        wrap.innerHTML = "<p>No labels found.</p>";
        return;
      }

      fetchPosts(labels[0], count, (posts) => {
        posts.slice(0, 3).forEach((p) => {
          wrap.innerHTML += buildCard(p);
        });
      });
    });
  }

  if (document.readyState != "loading") init();
  else document.addEventListener("DOMContentLoaded", init);
})();



//--------------------------Category-------------------------//

// Linklists Slider
document.addEventListener("DOMContentLoaded", function () {
    const sliderConfigs = [
        {
            id: "#LinkList5",
            swiperClass: "myCategorySwiper-0",
            prevClass: "swiper-button-prev-0",
            nextClass: "swiper-button-next-0",
            paginationClass: "swiper-pagination-0",
            options: {
                slidesPerView: 3,
                spaceBetween: 20,
                loop: true,
                breakpoints: {
                    0: { slidesPerView: 1 },
                    150: { slidesPerView: 2 },
                    450: { slidesPerView: 3 },
                    768: { slidesPerView: 4 },
                    1024: { slidesPerView: 4 },
                    1340: { slidesPerView: 5 },
                }
            }
        }
    ];

    sliderConfigs.forEach((config) => {
        const widget = document.querySelector(config.id + " .widget-content");
        if (!widget) return;

        const ul = widget.querySelector("ul");
        if (!ul) return;

        const items = ul.querySelectorAll("li");
        if (!items.length) return;

        // Create Swiper structure
        const swiperContainer = document.createElement("div");
        swiperContainer.classList.add("swiper", config.swiperClass);

        const swiperWrapper = document.createElement("div");
        swiperWrapper.classList.add("swiper-wrapper");

        // Convert each <li> into a .swiper-slide
        items.forEach((li) => {
            const link = li.querySelector("a");
            const img = li.querySelector("img");
            if (!link || !img) return;

            const text = link.textContent.trim();
            const slide = document.createElement("div");
            slide.classList.add("swiper-slide");

            const clonedLink = link.cloneNode(true);
            clonedLink.setAttribute("data-label", text);
            clonedLink.innerHTML = "";
            clonedLink.appendChild(img.cloneNode(true));

            slide.appendChild(clonedLink);
            swiperWrapper.appendChild(slide);
        });

        // Add navigation & pagination
        const prev = document.createElement("div");
        prev.className = "swiper-button-prev " + config.prevClass;

        const next = document.createElement("div");
        next.className = "swiper-button-next " + config.nextClass;

        const pagination = document.createElement("div");
        pagination.className = "swiper-pagination " + config.paginationClass;

        swiperContainer.appendChild(swiperWrapper);
        swiperContainer.appendChild(prev);
        swiperContainer.appendChild(next);
        swiperContainer.appendChild(pagination);

        // Replace content
        widget.innerHTML = "";
        widget.appendChild(swiperContainer);

        // Initialize Swiper
        new Swiper("." + config.swiperClass, {
            slidesPerView: config.options.slidesPerView,
            spaceBetween: config.options.spaceBetween,
            loop: config.options.loop,
            navigation: {
                nextEl: "." + config.nextClass,
                prevEl: "." + config.prevClass,
            },
            pagination: {
                el: "." + config.paginationClass,
                clickable: true,
                renderBullet: function (index, className) {
                    return '<span class="' + className + '" data-index="' + index + '"></span>';
                },
                dynamicBullets: true,
                dynamicMainBullets: 1,
            },
            breakpoints: config.options.breakpoints
        });
    });
});




