//----------------------------------Error 404------------------------------------//

document.addEventListener("DOMContentLoaded", function() {
  // Pages where we want to hide breadcrumbs & status message
  const hideOnPages = [
    "/p/checkout.html"
  ];

  // Get current page path (everything after the domain)
  const currentPath = window.location.pathname;

  if (hideOnPages.includes(currentPath)) {
    // Hide breadcrumbs
    const breadcrumbs = document.querySelector(".static-breadcrums");
    if (breadcrumbs) {
      breadcrumbs.style.display = "none";
    }

    // Hide status message
    const statusMsg = document.querySelector(".status-msg-wrap");
    if (statusMsg) {
      statusMsg.style.display = "none";
    }
  }
});




