//-------------------------Cart--------------------------------------//

document.addEventListener("DOMContentLoaded", function () {
    function activateCartPanel() {
        const cartProducts = document.getElementById("CartProducts");
        const overlay = document.getElementById("Overlay");
        if (cartProducts && overlay) {
            cartProducts.classList.add("active");
            overlay.classList.add("active");
            document.body.style.overflow = "hidden";
            const wishlistProducts = document.querySelector("#WishlistProducts");
            if (wishlistProducts) wishlistProducts.classList.remove("active");
        }
    }

    const cart = {
        items: [],
        addItem: function (product, quantity = 1) {
            try {
                const existingIndex = this.items.findIndex(
                    (item) => item.id === product.id
                );
                if (existingIndex >= 0) {
                    this.items[existingIndex].quantity += quantity;
                } else {
                    this.items.push({ ...product, quantity: quantity });
                }
                this.save();
                this.updateUI();
                if (
                    !window.isBuyNowFlow &&
                    !window.location.pathname.includes("/p/cart.html")
                ) {
                    activateCartPanel();
                }
                return true;
            } catch (e) {
                console.error("Error adding to cart:", e);
                return false;
            }
        },
        removeItem: function (index) {
            this.items.splice(index, 1);
            this.save();
            this.updateUI();
        },
        updateQuantity: function (index, change) {
            const newQty = this.items[index].quantity + change;
            if (newQty > 0 && newQty <= 100) {
                this.items[index].quantity = newQty;
            } else if (newQty < 1) {
                this.removeItem(index);
            } else {
                this.items[index].quantity = 100;
            }
            this.save();
            this.updateUI();
        },
        save: function () {
            localStorage.setItem("simpleCart", JSON.stringify(this.items));
        },
        load: function () {
            try {
                const saved = localStorage.getItem("simpleCart");
                this.items = saved ? JSON.parse(saved) : [];
                this.updateUI();
            } catch (e) {
                console.error("Error loading cart:", e);
                this.items = [];
            }
        },
        calculateTotal: function () {
            return this.items.reduce((total, item) => {
                const price =
                    parseFloat(item.currentPrice.replace(/[^0-9.-]/g, "")) || 0;
                return total + price * item.quantity;
            }, 0);
        },
        updateUI: function () {
            try {
                this.updateOffCanvasCart();
            } catch (e) {
                console.error("Error updating cart UI:", e);
            }
        },
        updateOffCanvasCart: function () {
            const cartMenu = document.getElementById("CartMenu");
            if (!cartMenu) return;
            const container =
                cartMenu.querySelector(".cart-items") || document.createElement("div");
            container.className = "cart-items";
            const cartCount = cartMenu.querySelector(".cart-count");
            const cartProducts = cartMenu.querySelector("#CartProducts");
            const panelButtons = cartProducts.querySelector(".panel-buttons");
            let totalAmountElement = cartProducts.querySelector(
                ".total-amount .total-value"
            );
            if (!totalAmountElement) {
                totalAmountElement = this.createTotalAmountElement();
            }
            const totalItems = this.items.reduce(
                (sum, item) => sum + item.quantity,
                0
            );
            if (cartCount) {
                cartCount.textContent = totalItems;
            }
            container.innerHTML = "";
            if (this.items.length > 0) {
                const emptyState = cartProducts.querySelector(".empty-state");
                if (emptyState) emptyState.remove();
                if (!container.parentNode) {
                    cartProducts.insertBefore(container, panelButtons);
                }
                this.items.forEach((item, index) => {
                    const itemElement = document.createElement("div");
                    itemElement.className = "cart-item";
                    itemElement.innerHTML = `
                        <img src="${item.image || "https://via.placeholder.com/70?text=No+Image"}" loading="lazy" alt="${item.title}">
                        <div class="cart-item-details">
                            <div class="cart-item-title">${item.title || "Untitled Product"}</div>
                            <div class="cart-item-price">
                                <span class="current-price">${item.currentPrice || "$0.00"}</span>
                            </div>
                            <div class="cart-item-quantity">
                                <span>Qty: ${item.quantity}</span>
                            </div>
                            <button class="remove-item-btn" data-index="${index}"></button>
                        </div>
                    `;
                    container.appendChild(itemElement);
                });
                const total = this.calculateTotal();
                totalAmountElement.textContent = `$${total.toFixed(2)}`;
                totalAmountElement.style.display = "block";
            } else {
                const emptyState = cartProducts.querySelector(".empty-state");
                if (!emptyState) {
                    const newEmptyState = document.createElement("div");
                    newEmptyState.className = "empty-state";
                    newEmptyState.innerHTML = `
                        <i class="bi bi-bag-x"></i>
                        <p>Your cart is currently empty</p>
                    `;
                    cartProducts.insertBefore(newEmptyState, panelButtons);
                }
                totalAmountElement.style.display = "none";
            }
        },
        createTotalAmountElement: function () {
            const cartProducts = document.querySelector("#CartProducts");
            const panelButtons = cartProducts.querySelector(".panel-buttons");
            const totalElement = document.createElement("div");
            totalElement.className = "total-amount";
            totalElement.style.display = "none";
            totalElement.innerHTML = `
                <div class="total-row">
                    <span class='subtotal'>Subtotal:</span>
                    <span class="total-value">$0.00</span>
                </div>
            `;
            cartProducts.insertBefore(totalElement, panelButtons);
            return totalElement.querySelector(".total-value");
        },
    };

    cart.load();

    function handleQuantityChange(input, change) {
        if (!input) return;
        let newQty = parseInt(input.value) + change;
        newQty = Math.max(1, Math.min(100, newQty));
        input.value = newQty;
    }

    document.addEventListener("click", function (e) {
        if (
            e.target.classList.contains("quantity-btn") &&
            !e.target.hasAttribute("data-index")
        ) {
            const isMinus = e.target.classList.contains("qty-minus");
            const isPlus = e.target.classList.contains("qty-plus");
            const input = e.target.closest(".vivid-quantity-selector")?.querySelector("#product-qty");
            if (input && (isMinus || isPlus)) {
                handleQuantityChange(input, isMinus ? -1 : 1);
            }
        }

        const addToCartBtn = e.target.closest(".add-to-cart");
        if (addToCartBtn) {
            e.preventDefault();
            try {
                const postId = addToCartBtn.getAttribute("data-id") || "missing-id";
                const postTitle =
                    addToCartBtn.getAttribute("data-title") || "Untitled Product";
                const productContainer =
                    addToCartBtn.closest(".post-outer") ||
                    addToCartBtn.closest(".post") ||
                    document;
                let currentPrice =
                    productContainer
                        .querySelector(".vivid-product-current-price")
                        ?.textContent?.trim() ||
                    productContainer
                        .querySelector("[data-vivid-product-current-price]")
                        ?.textContent?.trim() ||
                    productContainer
                        .querySelector("[data-vivid-product-current-price]")
                        ?.getAttribute("data-vivid-product-current-price") ||
                    productContainer
                        .querySelector(".current-price")
                        ?.textContent?.trim() ||
                    "$0.00";
                let image = "";
                // Check if on homepage or full post view
                if (document.querySelector("#VividHomepageView")) {
                    // Homepage view
                    image = productContainer.querySelector("#PostImage img")?.src || "";
                } else {
                    // Full post view
                    const mainSlider = productContainer.querySelector(".vivid-main-slider");
                    image =
                        mainSlider?.querySelector(".swiper-slide-active img")?.src ||
                        mainSlider?.querySelector(".swiper-slide img")?.src ||
                        "";
                }
                const quantityInput = productContainer.querySelector("#product-qty");
                const quantity = quantityInput ? parseInt(quantityInput.value) || 1 : 1;

                cart.addItem(
                    {
                        id: postId,
                        title: postTitle,
                        image: image,
                        currentPrice: currentPrice,
                    },
                    quantity
                );

                const originalHTML = addToCartBtn.innerHTML;
                addToCartBtn.innerHTML = '<i class="fas fa-check"></i> Added!';
                addToCartBtn.style.pointerEvents = "none";
                setTimeout(() => {
                    addToCartBtn.innerHTML = originalHTML;
                    addToCartBtn.style.pointerEvents = "";
                }, 2000);
            } catch (error) {
                console.error("Error adding to cart:", error);
            }
        }

        if (e.target.classList.contains("remove-btn")) {
            const index = parseInt(e.target.getAttribute("data-index"));
            cart.removeItem(index);
        }
    });

    const cartMenu = document.getElementById("CartMenu");
    if (cartMenu) {
        const cartIcon = cartMenu.querySelector(".cart-icon");
        const cartProducts = cartMenu.querySelector("#CartProducts");
        const overlay = document.getElementById("Overlay");
        cartIcon.addEventListener("click", function (e) {
            e.stopPropagation();
            if (!window.location.pathname.includes("/p/cart.html")) {
                activateCartPanel();
            }
        });

        const cartClose = cartProducts.querySelector(".close-panel");
        if (cartClose) {
            cartClose.addEventListener("click", function () {
                cartProducts.classList.remove("active");
                overlay.classList.remove("active");
                document.body.style.overflow = "";
            });
        }
    }

    const overlay = document.getElementById("Overlay");
    if (overlay) {
        overlay.addEventListener("click", function () {
            document
                .querySelectorAll("#WishlistProducts, #CartProducts")
                .forEach((panel) => {
                    panel.classList.remove("active");
                });
            overlay.classList.remove("active");
            document.body.style.overflow = "";
        });
    }

    document.addEventListener("click", function (e) {
        if (
            e.target.classList.contains("notcheckout") ||
            e.target.id === "HavingNotCheckoutpage"
        ) {
            e.preventDefault();
            if (cart.items.length > 0) {
                alert(
                    "Proceeding to checkout with " +
                    cart.items.reduce((sum, item) => sum + item.quantity, 0) +
                    " items"
                );
            } else {
                alert("Your cart is empty");
            }
        }
    });
});




/*-----------------------Fullpage View Slider----------------------*/

document.addEventListener('DOMContentLoaded', () => {
    const postBody = document.querySelector(".post-body");
    const sliderWrapper = document.querySelector('.vivid-fullpage-slider');
    const loadingIndicator = document.querySelector('.vivid-loading');

    if (!postBody || !sliderWrapper) {
        if (loadingIndicator) loadingIndicator.style.display = 'none';
        if (sliderWrapper) sliderWrapper.style.display = 'none';
        console.error("Required .post-body or .vivid-fullpage-slider element not found.");
        return;
    }

    const media = [];
    const imageUrls = new Set();
    const postImages = postBody.querySelectorAll("img");

    postImages.forEach(img => {
        let src = img.getAttribute("src");
        const alt = img.getAttribute("alt") || "Product Image";

        if (src) {
            // Replace any width-height specifier with original size
            src = src.replace(/\/w\d+-h\d+\//, '/s0/');

            if (!imageUrls.has(src)) {
                imageUrls.add(src);
                media.push({ type: 'image', src: src, alt: alt });
            }
        }

        const parent = img.parentElement;
        if (parent && parent.classList.contains('separator')) {
            parent.style.display = 'none';
        } else {
            img.style.display = 'none';
        }
    });

    postBody.querySelectorAll(".separator").forEach(separator => {
        separator.style.display = "none";
        const prev = separator.previousElementSibling;
        const next = separator.nextElementSibling;
        if (prev && prev.tagName === "BR") { prev.style.display = "none"; }
        if (next && next.tagName === "BR") { next.style.display = "none"; }
    });

    postBody.querySelectorAll("p, div").forEach(el => {
        const html = el.innerHTML.trim().toLowerCase();
        if (el.textContent.trim() === "" && (html === "" || html === "<br>" || html === "<br/>" || html === "<br />")) {
            el.style.display = "none";
        }
    });

    if (media.length === 0) {
        if (loadingIndicator) loadingIndicator.style.display = "none";
        sliderWrapper.style.display = 'none';
        return;
    }

    const mainWrapper = document.getElementById('vividMainSlider');
    const thumbWrapper = document.getElementById('vividThumbSlider');

    const preloadMedia = () => {
        let loadedCount = 0;
        media.forEach(item => {
            const img = new Image();
            img.src = item.src;
            img.onload = img.onerror = () => {
                loadedCount++;
                if (loadedCount === media.length) {
                    if (loadingIndicator) loadingIndicator.style.display = 'none';
                }
            };
        });
    };

    const createSlides = () => {
        media.forEach((item, index) => {
            const mainSlide = document.createElement('div');
            mainSlide.className = 'swiper-slide';
            mainSlide.innerHTML = `
                <div class="zoom-container" data-index="${index}">
                    <img src="${item.src}" alt="${item.alt}" class="main-image" loading="lazy"/>
                    <div class="controls">
                        <div class="vivid-control-btn fullscreen-btn" data-index="${index}" aria-label="Enter fullscreen">
                            <i class="fas fa-expand"></i>
                        </div>
                    </div>
                </div>`;
            mainWrapper.appendChild(mainSlide);

            const thumbSlide = document.createElement('div');
            thumbSlide.className = 'swiper-slide';
            thumbSlide.innerHTML = `<img src="${item.src}" alt="${item.alt}" loading="lazy"/>`;
            thumbWrapper.appendChild(thumbSlide);
        });
    };

    let mainSwiper, thumbSwiper;

    const initializeSwiper = () => {
        thumbSwiper = new Swiper('.vivid-thumb-slider', {
            slidesPerView: 'auto',
            spaceBetween: 10,
            freeMode: true,
            watchSlidesProgress: true,
            navigation: {
                nextEl: '.vivid-slider-nav.next',
                prevEl: '.vivid-slider-nav.prev',
            }
        });

        mainSwiper = new Swiper('.vivid-main-slider', {
            loop: media.length > 1,
            thumbs: {
                swiper: thumbSwiper
            },
            speed: 800,
            grabCursor: true,
            effect: 'slide',
            spaceBetween: '20px'
        });
    };

    const initializeFullscreen = () => {
        const fullscreenOverlay = document.querySelector('.vivid-fullscreen');
        const fullscreenClose = document.querySelector('.vivid-fullscreen-close');
        const fullscreenNavPrev = document.querySelector('.fullscreen-main-nav.prev');
        const fullscreenNavNext = document.querySelector('.fullscreen-main-nav.next');

        // Create navigation buttons if they don't exist
        if (!fullscreenNavPrev && !fullscreenNavNext) {
            const navPrev = document.createElement('div');
            navPrev.className = 'fullscreen-main-nav prev';
            navPrev.innerHTML = '<i class="fas fa-chevron-left"></i>';
            fullscreenOverlay.appendChild(navPrev);

            const navNext = document.createElement('div');
            navNext.className = 'fullscreen-main-nav next';
            navNext.innerHTML = '<i class="fas fa-chevron-right"></i>';
            fullscreenOverlay.appendChild(navNext);
        }

        let currentImageIndex = 0;

        const updateFullscreenMedia = (index) => {
            currentImageIndex = (index + media.length) % media.length;
            const item = media[currentImageIndex];
            const imgElement = document.querySelector('.vivid-fullscreen-img');
            imgElement.src = item.src;
            imgElement.alt = item.alt;
            if (mainSwiper) {
                mainSwiper.slideToLoop(currentImageIndex);
            }
        };

        // Add fullscreen button event listeners
        document.addEventListener('click', function(e) {
            if (e.target.closest('.fullscreen-btn')) {
                const btn = e.target.closest('.fullscreen-btn');
                const index = parseInt(btn.getAttribute('data-index'), 10);
                updateFullscreenMedia(index);
                fullscreenOverlay.classList.add('active');
                document.body.style.overflow = 'hidden';
            }
        });

        // Previous button
        document.querySelector('.fullscreen-main-nav.prev').addEventListener('click', (e) => {
            e.stopPropagation();
            updateFullscreenMedia(currentImageIndex - 1);
        });

        // Next button
        document.querySelector('.fullscreen-main-nav.next').addEventListener('click', (e) => {
            e.stopPropagation();
            updateFullscreenMedia(currentImageIndex + 1);
        });

        // Close button - fixed with proper event handling
        if (fullscreenClose) {
            fullscreenClose.addEventListener('click', (e) => {
                e.stopPropagation();
                fullscreenOverlay.classList.remove('active');
                document.body.style.overflow = '';
            });
        }

        // Also close when clicking on the overlay background
        if (fullscreenOverlay) {
            fullscreenOverlay.addEventListener('click', (e) => {
                if (e.target === fullscreenOverlay) {
                    fullscreenOverlay.classList.remove('active');
                    document.body.style.overflow = '';
                }
            });
        }

        // Close with Escape key
        document.addEventListener('keydown', (e) => {
            if (e.key === 'Escape' && fullscreenOverlay.classList.contains('active')) {
                fullscreenOverlay.classList.remove('active');
                document.body.style.overflow = '';
            }
        });
    };

    preloadMedia();
    createSlides();
    initializeSwiper();
    initializeFullscreen();
});
//-----------------Footer Copy Rights --------------------//

(function() {
  // Create a shadow DOM container
  const container = document.createElement("div");
  document.body.appendChild(container);

  // Attach shadow root (isolated DOM and CSS)
  const shadow = container.attachShadow({ mode: "closed" });

  // Add the footer HTML + CSS inside shadow DOM
  shadow.innerHTML = `
    <style>
      .footer-compact {
        font-size: 13px !important;
        color: white !important;
        text-align: center !important;
        padding: 22px 0 !important;
        margin: 0px !important;
        width: 100% !important;
        background: black !important;
        position: relative !important;
        z-index: 999999 !important;
        visibility: visible !important;
        cursor: text !important;
        pointer-events: auto !important;
        height: max-content !important;
      }
      .footer-compact a { 
        color: orange !important;
        text-decoration: none !important;
        font-weight: 900 !important;
        word-spacing: 5px !important;
        font-size: 14px !important;
        cursor: pointer !important;
        pointer-events: auto !important;
        visibility: visible !important;
        display: inline !important;
        position: relative !important;
        height: max-content !important;
        width: max-content !important;
      }
      .footer-compact a:hover {
        color: #f7ac23 !important;
      }
    </style>
    <p class='footer-compact'>
      &copy; <span id='y'></span> <span id='blog-name'></span> &#8212; 
      Template by <a href='https://templatedomain.example/' target='_blank'>TemplateName</a>. 
      Free to use &#8212; | 
      <a href='https://templatedomain.example/' target='_blank'>Buy Premium Version</a>
    </p>
  `;

  // Set year
  shadow.getElementById("y").textContent = new Date().getFullYear();

  // Set blog name
  const blogName =
    document.querySelector('meta[property="og:site_name"]')?.content ||
    document.title.trim();
  shadow.getElementById("blog-name").textContent = blogName;

  // Protect against removal/hiding
  const protect = () => {
    if (!document.body.contains(container)) {
      document.body.appendChild(container);
    }
  };
  new MutationObserver(protect).observe(document.body, { childList: true });

})();


