b:includable.script:
    (function() {
      var items = ;
      var msgs = ;
      var config = ;

// 
      var cursor = null;
      if (items && items.length > 0) {
        cursor = parseInt(items[items.length - 1].timestamp) + 1;
      }

      var bodyFromEntry = function(entry) {
        var text = (entry &&
                    ((entry.content && entry.content.$t) ||
                     (entry.summary && entry.summary.$t))) ||
            '';
        if (entry && entry.gd$extendedProperty) {
          for (var k in entry.gd$extendedProperty) {
            if (entry.gd$extendedProperty[k].name == 'blogger.contentRemoved') {
              return '<span class="deleted-comment">' + text + '</span>';
            }
          }
        }
        return text;
      }

      var parse = function(data) {
        cursor = null;
        var comments = [];
        if (data && data.feed && data.feed.entry) {
          for (var i = 0, entry; entry = data.feed.entry[i]; i++) {
            var comment = {};
            // comment ID, parsed out of the original id format
            var id = /blog-(\d+).post-(\d+)/.exec(entry.id.$t);
            comment.id = id ? id[2] : null;
            comment.body = bodyFromEntry(entry);
            comment.timestamp = Date.parse(entry.published.$t) + '';
            if (entry.author && entry.author.constructor === Array) {
              var auth = entry.author[0];
              if (auth) {
                comment.author = {
                  name: (auth.name ? auth.name.$t : undefined),
                  profileUrl: (auth.uri ? auth.uri.$t : undefined),
                  avatarUrl: (auth.gd$image ? auth.gd$image.src : undefined)
                };
              }
            }
            if (entry.link) {
              if (entry.link[2]) {
                comment.link = comment.permalink = entry.link[2].href;
              }
              if (entry.link[3]) {
                var pid = /.*comments\/default\/(\d+)\?.*/.exec(entry.link[3].href);
                if (pid && pid[1]) {
                  comment.parentId = pid[1];
                }
              }
            }
            comment.deleteclass = 'item-control blog-admin';
            if (entry.gd$extendedProperty) {
              for (var k in entry.gd$extendedProperty) {
                if (entry.gd$extendedProperty[k].name == 'blogger.itemClass') {
                  comment.deleteclass += ' ' + entry.gd$extendedProperty[k].value;
                } else if (entry.gd$extendedProperty[k].name == 'blogger.displayTime') {
                  comment.displayTime = entry.gd$extendedProperty[k].value;
                }
              }
            }
            comments.push(comment);
          }
        }
        return comments;
      };

      var paginator = function(callback) {
        if (hasMore()) {
          var url = config.feed + '?alt=json&v=2&orderby=published&reverse=false&max-results=50';
          if (cursor) {
            url += '&published-min=' + new Date(cursor).toISOString();
          }
          window.bloggercomments = function(data) {
            var parsed = parse(data);
            cursor = parsed.length < 50 ? null
                : parseInt(parsed[parsed.length - 1].timestamp) + 1
            callback(parsed);
            window.bloggercomments = null;
          }
          url += '&callback=bloggercomments';
          var script = document.createElement('script');
          script.type = 'text/javascript';
          script.src = url;
          document.getElementsByTagName('head')[0].appendChild(script);
        }
      };
      var hasMore = function() {
        return !!cursor;
      };
      var getMeta = function(key, comment) {
        if ('iswriter' == key) {
          var matches = !!comment.author
              && comment.author.name == config.authorName
              && comment.author.profileUrl == config.authorUrl;
          return matches ? 'true' : '';
        } else if ('deletelink' == key) {
          return config.baseUri + '/comment/delete/'
               + config.blogId + '/' + comment.id;
        } else if ('deleteclass' == key) {
          return comment.deleteclass;
        }
        return '';
      };

      var replybox = null;
      var replyUrlParts = null;
      var replyParent = undefined;

      var onReply = function(commentId, domId) {
        if (replybox == null) {
          // lazily cache replybox, and adjust to suit this style:
          replybox = document.getElementById('comment-editor');
          if (replybox != null) {
            replybox.height = '250px';
            replybox.style.display = 'block';
            replyUrlParts = replybox.src.split('#');
          }
        }
        if (replybox && (commentId !== replyParent)) {
          replybox.src = '';
          document.getElementById(domId).insertBefore(replybox, null);
          replybox.src = replyUrlParts[0]
              + (commentId ? '&parentID=' + commentId : '')
              + '#' + replyUrlParts[1];
          replyParent = commentId;
        }
      };

      var hash = (window.location.hash || '#').substring(1);
      var startThread, targetComment;
      if (/^comment-form_/.test(hash)) {
        startThread = hash.substring('comment-form_'.length);
      } else if (/^c[0-9]+$/.test(hash)) {
        targetComment = hash.substring(1);
      }

      // Configure commenting API:
      var configJso = {
        'maxDepth': config.maxThreadDepth
      };
      var provider = {
        'id': config.postId,
        'data': items,
        'loadNext': paginator,
        'hasMore': hasMore,
        'getMeta': getMeta,
        'onReply': onReply,
        'rendered': true,
        'initComment': targetComment,
        'initReplyThread': startThread,
        'config': configJso,
        'messages': msgs
      };

      var render = function() {
        if (window.goog && window.goog.comments) {
          var holder = document.getElementById('comment-holder');
          window.goog.comments.render(holder, provider);
        }
      };

      // render now, or queue to render when library loads:
      if (window.goog && window.goog.comments) {
        render();
      } else {
        window.goog = window.goog || {};
        window.goog.comments = window.goog.comments || {};
        window.goog.comments.loadQueue = window.goog.comments.loadQueue || [];
        window.goog.comments.loadQueue.push(render);
      }
    })();
//

body.script:
//


        const checkoutCart = {
            getCart: function () {
                try {
                    const cart = localStorage.getItem('simpleCart');
                    if (!cart || cart === '[]') return [];
                    const parsedCart = JSON.parse(cart);
                    return Array.isArray(parsedCart) ? parsedCart.map(item => ({
                        id: item.id || 'missing-id',
                        title: item.title || 'Untitled Product',
                        image: item.image || 'https://via.placeholder.com/80?text=No+Image',
                        currentPrice: item.currentPrice || '$0.00',
                        quantity: item.quantity || 1
                    })) : [];
                } catch (e) {
                    console.error('Error loading cart:', e);
                    return [];
                }
            },
            getCurrency: function () {
                const cart = this.getCart();
                if (cart.length > 0) {
                    const price = cart[0].currentPrice || '$0.00';
                    const match = price.match(/[$Rs]*/);
                    return match && match[0] ? match[0] : '$';
                }
                return '$';
            },
            calculateTotals: function () {
                const cart = this.getCart();
                let subtotal = 0;
                cart.forEach(item => {
                    const price = parseFloat(item.currentPrice.replace(/[^0-9.-]/g, '')) || 0;
                    subtotal += price * item.quantity;
                });
                const shipping = this.calculateShipping(subtotal);
                const total = subtotal + shipping;
                return { subtotal, shipping, total };
            },
            calculateShipping: function (subtotal) {
                const { cost, rateType } = getThemeShippingSettings();
                if (subtotal === 0 || cost === 0) return 0;

                const cart = this.getCart();
                let shipping = 0;

                if (rateType === 'Per Item') {
                    const itemCount = cart.length;
                    shipping = cost * itemCount;
                } else if (rateType === 'Per Quantity') {
                    const totalQty = cart.reduce((sum, item) => sum + (item.quantity || 1), 0);
                    shipping = cost * totalQty;
                }

                return shipping;
            },
            updateOrderSummary: function () {
                const cart = this.getCart();
                const cartItemsContainer = document.getElementById('cart-items');
                const checkoutForm = document.getElementById('checkout-form');
                const confirmationMessage = document.getElementById('confirmation-message');
                if (!cartItemsContainer) return;

                const currency = this.getCurrency();
                cartItemsContainer.innerHTML = '';
                if (cart.length === 0) {
                    cartItemsContainer.innerHTML = '<p>No items in cart.</p>';
                } else {
                    cart.forEach(item => {
                        const price = parseFloat(item.currentPrice.replace(/[^0-9.-]/g, '')) || 0;
                        const itemTotal = price * item.quantity;
                        const itemElement = document.createElement('div');
                        itemElement.className = 'cart-item';
                        itemElement.innerHTML = `
                            <p>${item.title} - ${item.currentPrice} x ${item.quantity} = ${currency}${itemTotal.toFixed(2)}</p>
                        `;
                        cartItemsContainer.appendChild(itemElement);
                    });
                }

                const { cost, rateType, rawCost } = getThemeShippingSettings();
                const totals = this.calculateTotals();

                document.getElementById('cart-subtotal').textContent = `${currency}${totals.subtotal.toFixed(2)}`;
                document.getElementById('shipping-price').textContent = rawCost || 'Free';
                document.getElementById('shipping-rate').textContent = rateType || 'Per Item';
                document.getElementById('cart-shipping').textContent = totals.shipping === 0 ? 'Free' : `${currency}${totals.shipping.toFixed(2)}`;
                document.getElementById('cart-total').textContent = `${currency}${totals.total.toFixed(2)}`;
            }
        };

        function getThemeShippingSettings() {
            const settingsRoot = document.querySelector('#vivid-theme-settings');
            let cost = 0;
            let rateType = 'Per Item';
            let rawCost = 'Free';

            if (settingsRoot) {
                const anchors = settingsRoot.querySelectorAll('a');
                anchors.forEach(a => {
                    const txt = (a.textContent || '').trim().toLowerCase();
                    const href = (a.getAttribute('href') || '').trim();

                    if (txt.includes('shipping cost')) {
                        rawCost = href;
                        const clean = href.replace(/[^0-9.,a-z$]/gi, '').toLowerCase();
                        if (clean === 'free') {
                            cost = 0;
                        } else {
                            const num = parseFloat(clean.replace(/[^0-9.]/g, ''));
                            if (!isNaN(num)) cost = num;
                        }
                    }

                    if (txt.includes('shipping rate')) {
                        if (/quantity/i.test(href)) {
                            rateType = 'Per Quantity';
                        } else if (/item/i.test(href)) {
                            rateType = 'Per Item';
                        }
                    }
                });
            }

            return { cost, rateType, rawCost };
        }

        function generateOrderId() {
            const orders = JSON.parse(localStorage.getItem('bloggerStoreOrders') || '[]');
            const orderCount = orders.length + 1;
            return `ORD-${1000 + orderCount}`;
        }

        function validateEmail(email) {
            return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
        }

        function validateForm() {
            let isValid = true;
            const fields = ['email', 'name', 'address', 'city', 'state', 'country', 'zip'];
            fields.forEach(field => {
                const input = document.getElementById(field);
                const error = document.getElementById(`${field}-error`);
                if (field === 'email' && !validateEmail(input.value)) {
                    error.classList.add('show');
                    input.classList.add('input-error');
                    isValid = false;
                } else if (!input.value.trim()) {
                    error.classList.add('show');
                    input.classList.add('input-error');
                    isValid = false;
                } else {
                    error.classList.remove('show');
                    input.classList.remove('input-error');
                }
            });
            return isValid;
        }

        document.addEventListener('DOMContentLoaded', function () {
            checkoutCart.updateOrderSummary();

            const checkoutForm = document.getElementById('checkout-form');
            const confirmationMessage = document.getElementById('confirmation-message');

            checkoutForm.addEventListener('submit', function (e) {
                e.preventDefault();
                if (!validateForm()) return;

                const cart = checkoutCart.getCart();
                if (cart.length === 0) {
                    alert('Your cart is empty.');
                    return;
                }

                const totals = checkoutCart.calculateTotals();
                const orderData = {
                    orderId: generateOrderId(),
                    email: document.getElementById('email').value || '',
                    phone: document.getElementById('phone').value || '',
                    shippingAddress: {
                        name: document.getElementById('name').value || '',
                        address: document.getElementById('address').value || '',
                        city: document.getElementById('city').value || '',
                        state: document.getElementById('state').value || '',
                        country: document.getElementById('country').value || '',
                        zip: document.getElementById('zip').value || ''
                    },
                    billingAddress: {
                        name: document.getElementById('name').value || '',
                        address: document.getElementById('address').value || '',
                        city: document.getElementById('city').value || '',
                        state: document.getElementById('state').value || '',
                        country: document.getElementById('country').value || '',
                        zip: document.getElementById('zip').value || ''
                    },
                    paymentMethod: 'Cash on Delivery',
                    items: cart,
                    subtotal: totals.subtotal,
                    shipping: totals.shipping,
                    total: totals.total,
                    date: new Date().toISOString(),
                    status: 'Pending'
                };

                let orders = JSON.parse(localStorage.getItem('bloggerStoreOrders') || '[]');
                orders.push(orderData);
                localStorage.setItem('bloggerStoreOrders', JSON.stringify(orders));
                localStorage.removeItem('simpleCart');

                const cartCount = document.querySelector('.cart-count');
                if (cartCount) cartCount.textContent = '0';

                checkoutForm.style.display = 'none';
                confirmationMessage.style.display = 'block';
                checkoutCart.updateOrderSummary();
            });

            document.querySelectorAll('.input-field').forEach(input => {
                input.addEventListener('input', function () {
                    const error = document.getElementById(`${this.id}-error`);
                    if (this.id === 'email' && this.value.trim() && !validateEmail(this.value)) {
                        error.classList.add('show');
                        this.classList.add('input-error');
                    } else if (this.value.trim()) {
                        error.classList.remove('show');
                        this.classList.remove('input-error');
                    }
                });
            });

            const checkoutPage = document.getElementById('CheckoutPage');
            if (checkoutPage) {
                const isCheckoutPage = window.location.pathname.includes('/p/checkout.html');
                checkoutPage.style.display = isCheckoutPage ? 'block' : 'none';
            }
        });
//
